INSERT INTO `bc73q_content` VALUES(17, 102, 'Dětská fyzioterapie', 'detska-fyzioterapie', '<p>Dětská fyzioterapie nemusí být jen řešením již vzniklých chybných pohybových stereotypů, korekce plochonoží či poúrazová péče, ale i jako důležitá prevence před těmito důsledky. Posláním fyzioterapeutů v centru Kinisi je zachytit a ovlivnit odchylky ve vývoji dítěte v co nejmladším věku a nastavit tak jeho další zdravý růst.</p>\r\n\r\n<div class=\"row\">\r\n<div class=\"col-sm-6\">\r\n<ul class=\"list\">\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=195&amp;catid=88\" title=\"Vyšetření\">Vyšetření</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=196&amp;catid=88\" title=\"Indikace\">Indikace</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=197&amp;catid=88\" title=\"Metody a terapeutické přístupy\">Metody a terapeutické přístupy</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n', '', 1, 18, '2017-10-08 20:11:14', 630, '', '2017-11-21 16:36:29', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"left\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"none\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",