INSERT INTO `bc73q_content` VALUES(18, 103, 'Akutní a pooperační fyzioterapie', 'akutni-a-pooperacni-fyzioterapie', '<p>Akutní bolesti pohybového systému neustále přibývají a narůstá také jejich závažnost. Nejdůležitejším cílem akutní a pooperační fyzioterapie je zvládnutí bolesti, otoku, obnova a zachování plného rozsahu pohybu a péče o jizvu.</p>\r\n\r\n<div class=\"row\">\r\n<div class=\"col-sm-6\">\r\n<ul class=\"list\">\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=198&amp;catid=92\" title=\"Akutní fyzioterapie\">Akutní fyzioterapie</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=200&amp;catid=92\" title=\"Pooperační fyzioterapie\">Pooperační fyzioterapie</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n', '', 1, 73, '2017-10-08 20:11:14', 630, '', '2017-11-21 16:34:53', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"left\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"none\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options