INSERT INTO `bc73q_content` VALUES(50, 135, 'ACEMA Sparta Praha Florbal', 'acema-sparta-praha-florbal', '<p><i class=\"fa fa-quote-right\"></i>&nbsp;S fyzioterapeutkou Bc. Michaelou Hrickovou spolupracujeme, jako klub Acema Sparta Praha Florbal, již 5. sezónu. Kromě práce s A-týmem mužů má zkušenosti i s mladšími kategoriemi a to v rámci klubových fyziokonzultací, kde neřeší pouze akutní úrazy, ale i preventivní programy pro zmenšení rizik úrazů, vadné držení těla, jednostrannou zátěž a odstranění chybných pohybových stereotypů.<br />\r\nS prací fyzioterapeutky jsme po celou dobu spolupráce velice spokojeni.</p>\r\n', '', 1, 28, '2017-10-08 20:11:14', 630, '', '2017-11-12 12:11:31', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"images\\/testimonials\\/sparta-ref.jpg\",\"float_intro\":\"left\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"left\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"1\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', 13, 6, '', 'Reference na 