INSERT INTO `bc73q_content` VALUES(158, 727, 'Konzultace a podpora', 'koznultace-a-podpora', '<p>Jako zkušení fyzioterapeuti&nbsp;jsme klientovi pro případ potřeby konzultace stavu a jednotlivých cvičení neustále k dispozici. Obrátit se na nás můžete během léčebného/preventivního plánu s dotazem kdykoli budete potřebovat.</p>\r\n', '', 1, 70, '2017-10-08 20:11:14', 630, '', '2017-11-17 12:34:48', 850, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"thin-ico thin-icon-support\",\"float_intro\":\"\",\"image_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\",\"float_fulltext\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', 10, 1, '', 'V centru fyzioterapie jsme jako zkušení fyzioterapeuté jsme klientovi pro případ potřeby konzulatce stavu a jednotlivých cvičení neustále k dispozici. ', 1, 138, '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', 0, '*', '');
