INSERT INTO `bc73q_content` VALUES(192, 852, 'Fyzioterapeutické vyšetření dospelých', 'fyzioterapeuticke-vysetreni-dospelych', '<div class=\"row bottom-margin-small\">\r\n<div class=\"col-sm-6\">{os-gal-21}</div>\r\n\r\n<div class=\"col-sm-6\">\r\n<p><span class=\"span3 col-6\">Fyzioterapeutické vyšetření obecně označujeme jako kineziologický rozbor, který je zaměřený na funkci pohybové soustavy člověka a je základním předpokladem pro určení a nastavení správného a kontrolovaného terapeutického postupu. Současný stav pohybové soustavy je odrazem předchozích stavů, a proto se fyzioterapeutické vyšetření převážně zaměřuje na příznaky onemocnění, které jsme schopni terapeutickými metodami a postupy ovlivnit, resp. kompenzovat. Základními prostředky obecného vyšetření jsou stanovení anamnézy, palpační, aspekční a auskultační vyšetření a vyšetření antropometrické, která předcházejí vlastnímu popisu specializovaného vyšetření pohybového aparátu.</span></p>\r\n\r\n<h6>SOUVISEJÍCÍ</h6>\r\n\r\n<ul class=\"list\">\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=227&amp;catid=83\" title=\"Fyzioterapie dospělých (2)\">Základní informace</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=193&amp;catid=84\" title=\"Indikace\">Indikace</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=194&amp;catid=84\" title=\"Metodiky a terapeutické přístupy\">Metodiky a terapeutické přístupy</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n', '', 1, 84, '2017-10-08 20:11:14', 630, '', '2017-11-26 17:31:19', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"left\",\"image_fulltext_alt\":\"Dospe\\u030cla\\u0301 fyzio - vys\\u030cetr\\u030ceni\\u0301 SI kloubu\\u030a v ra\\u0301mci komplexni\\u0301ho vys\\u030cetr\\u030ceni\\u0301 dospe\\u030cly\\u0301ch\",\"image_fulltext_caption\":\"Dospe\\u030cla\\u0301 fyzio - vys\\u030cetr\\u030ceni\\u0301 SI kloubu\\u030a v ra\\u0301mci komplexni\\u0301ho vys\\u030cetr\\u030ceni\\u0301 dospe\\u030cly\\u0301ch\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', 53, 3, '', '', 1, 193, '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', 0, '*', '');
INSERT INTO `bc73q_content` VALUES(193, 853, 'Pomoc fyzioterapeuta', 'indikace', '<div class=\"row bottom-margin-small\">\r\n<div class=\"col-sm-6\">{os-gal-6}</div>\r\n\r\n<div class=\"col-sm-6\">\r\n<h6><strong><span class=\"span3 col-6\">S čím Vám může náš fyzioterapeut pomoct? </span></strong></h6>\r\n\r\n<p><span class=\"span3 col-6\">Máte chronické bolesti v bederní páteři, nebo bolesti&nbsp; mezi lopatkami? Úlohou fyzioterapeuta je určit zdroj vašich potíží a v co nejkratším čase je odstranit.</span></p>\r\n\r\n<h6>SOUVISEJÍCÍ</h6>\r\n\r\n<ul class=\"list\">\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=227&amp;catid=83\" title=\"Fyzioterapie dospělých (2)\">Základní informace</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=192&amp;catid=84\" title=\"Fyzioterapeutické vyšetření\">Vyšetření</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=194&amp;catid=84\" title=\"Metodiky a terapeutické přístupy\">Metodiky a terapeutické přístupy</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n\r\n<h4><strong><strong>Naše zaměření </strong></strong></h4>\r\n\r\n<div class=\"row\">\r\n<div class=\"col-sm-3\">\r\n<p><strong><i class=\"fa fa-dot-circle-o\"></i>&nbsp; </strong>Akutní i chronické bolesti zad(krční, hrudní i bederní páteře)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti zad po operaci páteře</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti hlavy</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Tinitus (hučení v uších), kinetóza motání hlavy),závratě</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Blokády kloubů a žeber</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Poúrazové stavy ( poranění svalů, kloubů, vazů a šlach)</p>\r\n</div>\r\n\r\n<div class=\"col-sm-3\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Pooperační stavy (zlomeniny kostí, výrony a luxace kloubů, operace menisků, vazů, výhřezy ploténky)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti ramen (impingement syndrom, syndrom rotatorové manžety, zamrznuté rameno, artróza ramene)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti kyčelních kloubů (koxartróza, bolesti v tříslech, femoroacetabulární impingement syndrom)</p>\r\n</div>\r\n\r\n<div class=\"col-sm-3\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Stavy po výměně kloubů (totální endoprotéza kolene, kyčle i ramene)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Skoliózy</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Entezopatie (tenisový, golfový loket, skokanské koleno)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Úžinové syndromy (syndrom karpálního tunelu, skalenový syndrom)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Premenstruační syndrom a bolestivá menstruace</p>\r\n</div>\r\n\r\n<div class=\"col-sm-3\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Degenerativní onemocnění kloubů (artrózy)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Neurologická onemocnění</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Přetížené svaly nebo fascie</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Vadné držení těla, centrální koordinační poruchy</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Poruchy v oblasti čelistního kloubu</p>\r\n</div>\r\n</div>\r\n', '', 1, 84, '2017-10-08 20:11:14', 630, '', '2017-11-26 16:32:30', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"0\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intr