INSERT INTO `bc73q_content` VALUES(219, 880, 'Synergická reflexní terapie (SRT)', 'synergicka-reflexni-terapie-srt', '<p>Speciální manuální technika, která vede k následnému odstranění chybných strukturálních a funkčních změn na pohybovém systému. Kombinuje reflexně terapeutické metody (myofasciální techniky a měkké techniky, akupresura, masáž reflexních zón, manipulace a mobilizace, korekce chybného postavení hlavy, trupu a končetin).</p>\r\n\r\n<p>Synergická reflexní terapie je léčebný koncept především pro sekundární pohybové poruchy u pacientů s primární poruchou centrální nervové soustavy. Její léčebný efekt je viditelný i u dalších poruch jako jsou vrozené vady pohybového aparátu, skoliózy, revmatoidní stavy, poúrazové a pooperační stavy. Je to metoda doplňková, podpůrná a preventivní. Působí léčebně na centrální i periférní nervovou soustavu a mentální i orgánové funkce. Je vhodná i pro ne zcela spolupracující pacienty.</p>\r\n\r\n<p>Má za cíl snížit tonus svalů, fascií a vazivových tkání, zmírnit patologické napínací reflexy, zabránit rozvoji kontraktur, udržet a obnovit trofiku tkání, zvětšit svalovou sílu, zlepšit funkci kloubů a optimalizovat zakřivení páteře.</p>\r\n\r\n<h4>Indikace</h4>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Mozečkové, spinální a periferní neuro-ortopedické pohybové poruchy (jako je např. dětská mozková obrna)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Genetické vady</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Vrozené i získané vady pohybového aparátu</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Cévní mozková příhoda</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Mozková traumata</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Poruchy řeči, učení, hyperaktivita</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Poruchy držení těla</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Následky artrózy a revmatických onemocnění</p>\r\n\r\n<h4>Kontraindikace</h4>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Vysoké teploty</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Krvácení do mozku</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Pooperační stavy</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Totální vyčerpanost organismu</p>\r\n', '', 1, 101, '2017-10-08 20:11:14', 630, '', '2017-11-25 00:21:34', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"left\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"none\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', 10, 5, '', 'Ve fyzioterapeutickém centru využíváme speciální manuální techniku STR vedoucí k odstranění chybných strukturálních a funkčních změn na pohybovém systému.', 1, 298, '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', 0, '*', '');
INSERT INTO `bc73q_content` VALUES(220, 881, 'Metoda Ludmily Mojžíšové', 'metoda-ludmily-mojzisove-v-nasem-fyzioterapeutickem-centru', '<div class=\"row bottom-margin-small\">\r\n<div class=\"col-sm-6\">{os-gal-31}</div>\r\n\r\n<div class=\"col-sm-6\">\r\n<p>Speciální manuální technika, která vede k následnému odstranění chybných strukturálních a funkčních změn na pohybovém systému.</p>\r\n\r\n<p>Metoda kombinuje reflexně terapeutické metody (myofasciální techniky a měkké techniky, akupresura, masáž reflexních zón, manipulace a mobilizace, korekce chybného postavení hlavy, trupu a končetin).</p>\r\n\r\n<p>Ludmila Mojžíšová byla terapeutka, zabývající se bolestmi páteře a léčbou neplodnosti. Svou léčbu zprvu aplikovala na sportovce v rámci svého působení na Fakultě tělesné výchovy a sportu. Později vyvinula vlastní metodu spočívající ve specifických cvicích, mobilizaci žeber a také uvolňování kostrče přes konečník. Léčila také mnoho žen s bolestmi zad, hlavy a s nepravidelným bolestivým menstruačním cyklem. Řadě pacientek, léčených zprvu pro pohybové potíže, se podařilo otěhotnět, ačkoliv předtím byly dlouze a bezvýsledně léčeny pro neplodnost. Zjištěním, že její terapie napomáhá léčit funkční sterilitu začala spolupracovat s gynekologem prof. Evženem Čechem.</p>\r\n</div>\r\n</div>\r\n\r\n<h4>Průběh terapie a cvičení podle Mojžíšové</h4>\r\n\r\n<p>Prostředky, které při terapii nejčastěji využíváme jsou aktivní cvičení dle Mojžíšové, uvolnění bolestivých spoušťových bodů (pánevního dna), elektroléčebné metody (termoterapie) a mobilizace s pasivními pohyby. Při první návštěvě v Kinisi si klienta pečlivě vyšetříme ve stoji, vleže na zádech a na břichu. Zhodnotíme postavení prvních sedmi žeber, bederní páteř i SI klouby a stav pánevního dna. Klienta naučíme základní sestavu cviků na doma, kterou cvičí dvakrát denně.<br />\r\nPři druhé terapii ošetříme pánevní dno, SI klouby, bederní páteř a blokády žeber. Průběh dalších terapií se odvíjí od rychlosti ústupu obtíží a spolupráci klienta.</p>\r\n\r\n<h4>Indikace metody Ludmily Mojžíšové</h4>\r\n\r\n<div class=\"row\">\r\n<div class=\"col-sm-4\">\r\n<h6><strong>Ortopedické indikace</strong></h6>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Skolióza</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti zad</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Pelvalgie (bolesti v pánevní oblasti)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Coccygodynie (bolesti kostrče)</p>\r\n</div>\r\n\r\n<div class=\"col-sm-4\">\r\n<h6><strong>Gynekologické indikace</strong></h6>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Neschopnost otěhotnět (funkční sterilita)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Neschopnost donosit plod (infertilita)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolestivá menstruace (dysmenorea)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Dyskomfort při sexu (dyspareunie)</p>\r\n</div>\r\n\r\n<div class=\"col-sm-4\">\r\n<h6><strong>Urologické indikace retence moče</strong></h6>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Inkontinence moče</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Interní indikace</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Obstipace</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"row\">\r\n<div class=\"col-sm-6\">\r\n<h4>Kontraindikace léčby</h4>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Akutní febrilní stavy</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Klienti po operaci srdce (od 6. týdnů po operaci)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Habituální luxace ramenního kloubu (při mobilizaci žeber)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Aktivní vnitřní hemeroidy (při ošetření pánevního dna)</p>\r\n</div>\r\n</div>\r\n', '', 1, 101, '2017-10-08 20:11:14', 630, '', '2017-11-26 15:44:35', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"left\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"Metoda Ludmily Moj\\u017e\\u00ed\\u0161ov\\u00e9\",\"image_fulltext_caption\":\"Metoda Ludmily Moj\\u017e\\u00ed\\u0161ov\\u00e9\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', 27, 4, '', 'Využíváme speciální manuální techniku Ludmily Mojžíšové, která vede k  odstranění chybných strukturálních a funkčních změn na pohybovém systému.', 1, 328, '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', 0, '*', '');
INSERT INTO `bc73q_content` VALUES(221, 882, 'Léčba suchou jehlou (Dry Needling)', 'lecba-suchou-jehlou-dry-needling', '<div class=\"row bottom-margin-small\">\r\n<div class=\"col-sm-6\">{os-gal-33}</div>\r\n\r\n<div class=\"col-sm-6\">\r\n<p>Při aplikaci suché jehly do spoušťového bolestivého bodu ve svale dochází k výraznému analgetickému účinku.</p>\r\n\r\n<p>Účinnost terapie se v některých případech neliší od léčby obstřiky. Nejvýrazněji se účinek projevuje, jakmile se jehla, kterou nemocný při aplikaci do svalu jinak vůbec nevnímá, dotkne bolestivého spoušťového bodu. Většinou klient ucítí prudkou bolest a ta odpovídá bolesti, kterou trpí (včetně přenesené bolesti). Dokážeme nahmatat i svalový záškub po kterém následuje uvolnění bolesti.</p>\r\n\r\n<p>V dnešní době řada ortopedů aplikuje ve své praxi kortikosteroidové injekce, které by se v některých případech (tenisový loket, golfový loket) neměly aplikovat, pokud jsme se nepřesvědčili, že je terapie suchou jehlou nedostatečná.</p>\r\n</div>\r\n</div>\r\n\r\n<h4>Průběh terapie suchou jehlou</h4>\r\n\r\n<p>Samotná terapie probíhá tak, že fyzioterapeut najde ve svalovém vlákně bolestivý spoušťový bod, do kterého následně aplikuje jehlu, a čeká na fenomén tání. Pak následuje kontrolní přebrnknutí přes bolestivé místo, při kterém pozoruje jestli záškub a bolesti vymizely. Některé spoušťové body jsou tzv. funkčně reverzibilní, tedy spontánně upravující se např. mobilizací daného kloubního segmentu, nebo aktivací hlubokého stabilizačního systému páteře. Jsou však i chronické bolestivé body které nereagují na běžné reflexní podněty. Poté můžeme aplikovat suchou jehlu anebo velmi silnou hlubokou masáž daného místa.</p>\r\n\r\n<h4>Indikace suché jehly</h4>\r\n\r\n<div class=\"row\">\r\n<div class=\"col-sm-4\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Akutní a chronické bolesti zad</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti hlavy</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Poúrazové a pooperační bolestivé stavy</p>\r\n</div>\r\n\r\n<div class=\"col-sm-4\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti ramen, kyčlí a kolen</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Pánevní bolesti, bolesti SI kloubů</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Úponové bolesti (tenisový loket, golfový loket a jiné entezopatie)</p>\r\n</div>\r\n\r\n<div class=\"col-sm-4\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Plantární fascitídy a patní ostruhy</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Svalové poranění</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Otoky</p>\r\n</div>\r\n</div>\r\n', '', 1, 101, '2017-10-08 20:11:14', 630, '', '2017-11-26 15:45:56', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"left\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"L\\u00e9\\u010dba suchou jehlou ve fyzioterapii\",\"image_fulltext_caption\":\"L\\u00e9\\u010dba suchou jehlou ve fyzioterapii\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', 17, 3, '', 'Terapie suchou jehlou spočívá v aplikaci suché jehly do spoušťového bolestivého bodu, čímž  ve svalu dochází k výraznému analgetickému účinku.', 1, 302, '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', 0, '*', '');
INSERT INTO `bc73q_content` VALUES(222, 883, 'Využití metody kineziotapingu ', 'vyuziti-metody-kineziotapingu', '<div class=\"row bottom-margin-small\">\r\n<div class=\"col-sm-6\">{os-gal-34}</div>\r\n\r\n<div class=\"col-sm-6\">\r\n<p>Metodu kineziotapingu vyvinul Dr. Kenso Kase v 70. letech. Tato metoda byla v posledních letech rozšířená, doplněná a zdokonalená. Velké oblibě se jí dostává hlavně ve sportovní sféře, kde je kineziotaping pro sportovce úspěšně využíván již několik let. Metoda je založená na aplikaci bavlněné elastické pásky na tělo člověka. Materiál a samotná aplikace pásky jsou navržené tak, aby nastartovali tělu vlastní léčebné procesy. Elastické pásky poskytují podporu a stabilitu svalů a kloubů bez jakéhokoliv omezení rozsahu pohybu.</p>\r\n\r\n<p>Pružná elastická páska je voděodolná, vzdušná a je nositelná dlouhou dobu (ideálně čtyř až sedmidenní rytmus). Před samotnou aplikací kineziotejpu je vhodné odmastit kůži. U mužů se doporučuje odstranit případné ochlupení pro maximální a optimální kontakt pásky na kůži. Standartní léčba kineziotapingem trvá jeden měsíc nebo i méně a dá se velmi dobře kombinovat s jinými rehabilitačními technikami.</p>\r\n</div>\r\n</div>\r\n\r\n<h4>Léčebné účinky</h4>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Zvyšuje se látková výměna, která urychluje regenerační proces</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Uvolňuje se svalové napětí (detonizace) a zvyšuje se svalová funkce (tonizace)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Analgetický účinek</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Reflexní účinek - tejpovaní akupresurních bodů</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Uvolňuje tuhost, otok kloubů a zlepšuje kloubní pohyblivost</p>\r\n\r\n<h4>Indikace kineziotapingu</h4>\r\n\r\n<div class=\"row\">\r\n<div class=\"col-sm-4\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Entezopatie (tenisový, golfový loket)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Úžinové syndromy (syndrom karpálního tunelu)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Natáhnutí, natrhnutí svalu</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Patologie v oblasti ramenního pletence (impingement syndrom, zmzlé rameno, artróza);</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti zad (bolesti hlavy, krční, hrudní a bederní páteře)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti třísel</p>\r\n</div>\r\n\r\n<div class=\"col-sm-4\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Zánět achilovky; ostruha patní; plantární fascitida</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Příčné i podélné plochonoží</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti&nbsp; kolene (atróza, skokanské koleno, bolest vnitřního, vnějšího postranního vazu)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Patologie v oblasti kyčelního kloubu;</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Akutní vymknutí kotníku; nestabilní kotník a rameno</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Astma</p>\r\n</div>\r\n\r\n<div class=\"col-sm-4\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Vybočený palec</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Menstruační bolesti; dysfunkce pánevního dna</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti čelisti; skřípání zubů</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Závratě</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Jizvy</p>\r\n</div>\r\n</div>\r\n\r\n<h4>Kontraindikace</h4>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>Léčba antibiotiky</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Akutní zánět</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Chronické neléčené nádorové onemocnění</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Akutní alergická reakce, tromboflebitida a trombóza</p>\r\n', '', 1, 101, '2017-10-08 20:11:14', 630, '', '2017-11-26 16:04:12', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"left\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"none\",\"image_fulltext_alt\":\"Vyu\\u017eit\\u00ed metody kineziotapingu \",\"image_fulltext_caption\":\"Vyu\\u017eit\\u00ed metody kineziotapingu \"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', 19, 2, '', 'V pražském fyzioteraputickém centru Kinisi využíváme metodu kineziotapingu, které se dostává velké obliby ve sportovní sféře i mimo ni.', 1, 315, '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', 0, '*', '');
INSERT INTO `bc73q_content` VALUES(223, 884, 'Ultrazvuk a elektroterapie', 'ultrazvuk-a-elektroterapie-v-nasem-fyzickoterapeutickem-centru', '<div class=\"row bottom-margin-small\">\r\n<div class=\"col-sm-6\">{os-gal-35}</div>\r\n\r\n<div class=\"col-sm-6\">\r\n<p>Přístrojovou terapii využíváme pro vyhledávání a odstraňování reflexních spoušťových bodů – trigger points, posttraumatické bolesti (výrony, hematomy, zlomeniny), revmatické nebo artrotické bolesti.</p>\r\n</div>\r\n</div>\r\n\r\n<h4>Elektroléčba</h4>\r\n\r\n<p>je část fyzikální terapie, při které se využívá léčebný účinek různých forem elektrické energie. Elektrický proud se dostává přes kůži do lidského těla pomocí přiložených elektrod a jeho léčebný efekt závisí na použité frekvenci. Optimální frekvenci indikuje na základě vyšetření erudovaný lékář, nebo fyzioterapeut. Nejčastěji se používají nízkofrekvenční a středofrekvenční proudy.&nbsp;</p>\r\n\r\n<h5>Elektroléčebné procedury</h5>\r\n\r\n<p>jsou bezpečné a používají se nejvíce při ortopedických, revmatologických, neurologických a cévních diagnózách. Mají účinek analgetický (protibolestivý), myorelaxační (uvolňující svalové spazmy), hyperemický (zlepšující prokrvení a antiedematózní (proti otokům).<br />\r\nPodle jednotlivých diagnóz se používá 7 - 10 aplikací, přičemž délka procedur je cca 20 minut.</p>\r\n\r\n<h4>Indikace</h4>\r\n\r\n<div class=\"row\">\r\n<div class=\"col-sm-4\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>Bolesti po úrazech a operacích</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolestivé svalové spazmy krční, hrudní a bederní páteře</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti při výhřezu plotének</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Dráždění periferních nervových kořenů</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Artróza velkých a malých kloubů (kyčel, koleno, hlezno, rameno, loket, ruka)</p>\r\n</div>\r\n\r\n<div class=\"col-sm-4\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolestivé rameno</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Tenisový a golfový loket</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti Achillovy šlachy</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Patní ostruha</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Kloubní revmatizmus</p>\r\n</div>\r\n\r\n<div class=\"col-sm-4\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti hlavy</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Migrény</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Poruchy prokrvení horních a dolních končetin</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Syndrom karpálního tunelu</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Diabetická noha&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<h4>Ultrazvuk - ultrasonoterapie</h4>\r\n\r\n<p>Ultrasonoterapie vychází z léčebného využití mechanické energie podélného vlnění s frekvencí nad 0,8 MHz, v současnosti prakticky 1,0-3,0 MHz. Ultrazvuk je podélné vlnění hmotného prostředí s frekvencí nad 20 000 Hz. Toto podélné vlnění je schopné dobře procházet měkkými tkáněmi do hloubky a v jednotlivých tkáních se různě absorbovat v závislosti na absorpčním koeficientu.</p>\r\n\r\n<h4>Kombinovaná terapie</h4>\r\n\r\n<p>Kombinovaná terapie (KT) je simultánní aplikace ultrazvuku a kontaktní elektroterapie. KT je v současnosti nejúčinnější metodou fyzikální terapie pro vyhledávání a odstraňování reflexních změn ve svalech (spoušťových bodů). Navíc dokáže objektivizovat tyto reflexní změny a jejich vymizení po manuálním, často i velmi vzdáleném zákroku.<br />\r\n&nbsp;<br />\r\nMezi hlavní účinky zařazujeme speciální myorelaxační účinek, cílený na momentální nejdráždivější vlákno pod místem aplikace. Parametry procedur nastavujeme podle toho či jdeme ošetřovat povrchový nebo hluboký sval těla.<br />\r\n&nbsp;<br />\r\nDoba aplikace - 1 minuta na každý nalezený spoušťový bod<br />\r\nPočet sezení - závisí od reakce na proceduru, ale pohybuje se v rozmezí 2-5x.</p>\r\n\r\n<h4>Kontraindikace</h4>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Parenchymatózní orgány- játra, slezina, mozek</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Vaječníky, varlata</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Plicní emfyzém- při aplikaci na hrudník</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Kostěné výstupky blízko pod povrchem</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Periferní nervy blízko pod povrchem</p>\r\n', '', 1, 101, '2017-10-08 20:11:14', 630, '', '2017-11-26 15:43:54', 630, 630, '2017-11-26 15:49:49', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"left\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"none\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', 24, 1, '', '', 1, 314, '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', 0, '*', '');
INSERT INTO `bc73q_content` VALUES(224, 885, 'Léčba TDP lampou ve fyzioterapii', 'lecba-tdp-lampou-ve-fyzioterapii', '<div class=\"row bottom-margin-small\">\r\n<div class=\"col-sm-6\">{os-gal-36}</div>\r\n\r\n<div class=\"col-sm-6\">\r\n<p>Spektrum terapeutického využití TDP lampy je široké.&nbsp; Tato přirozená metoda nemá žádné vedlejší účinky, protože spočívá především v samoregulaci tepla.</p>\r\n\r\n<p>TDP je čínska zkratka a znamená “Teding Diancibo Pu”, tedy “se speciálním elektromagnetickým spektrem”. Je kombinací prastarých principů tradiční čínské mediciny s moderní technologii.</p>\r\n\r\n<p>Klíčovou součástí TDP lampy je minerální plotna, která je pokrytá 33 minerály. Po nahřátí minerální plotny na určitou teplotu (cca 280°), začne vyzařovat jedinečné bio spektrum elektromagnetických vln v rozsahu 1-25 mikronů, které absorbuje lidské tělo. Nasátá energie podporuje mikrocirkulaci a metabolismus, posiluje imunitní systém a dosahuje krátkodobého i dlouhodobého zmírnění bolesti. Tyto vibrace vytváří biochemické reakce přes stimulaci rezonance biologických molekul buněk a buňkové tkáně, takže se opětovně vytváří přirozená rovnováha.</p>\r\n\r\n<p>V Kinisi lampu využíváme především pro relaxaci svalů před mobilizací při bolestech páteře. S úspěchem jí také aplikujeme u sportovců s bolestmi kloubů a zad.</p>\r\n</div>\r\n</div>\r\n\r\n<h4>Indikace</h4>\r\n\r\n<div class=\"row\">\r\n<div class=\"col-sm-4\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti hlavy, migréna</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti zad</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti žeber</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Kloubní blokáda</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Bolesti zubů</p>\r\n</div>\r\n\r\n<div class=\"col-sm-4\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Menstruační bolesti</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Parkinsonova choroba</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Osteoporóza</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Pooperační jizvy</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Proleženiny</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Kloubní bolesti</p>\r\n</div>\r\n\r\n<div class=\"col-sm-4\">\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Tinnitus (hučení v uších)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Kloubní zánět</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Natažení, natrhnutí svalu a vazů&lt;</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Entezopatie (tenisový, golfový loket, skokanské koleno)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Zánět Achillovy šlachy</p>\r\n</div>\r\n</div>\r\n\r\n<h4>Kontraindikace</h4>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Kardiostimulátor</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; U těhotných žen možno používat až po 3. měsíci těhotenství (vynechává se oblast břicha)</p>\r\n', '', 1, 101, '2017-10-08 20:11:14', 630, '', '2017-11-26 15:43:37', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"left\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"L\\u00e9\\u010dba TDP lampou\",\"image_fulltext_caption\":\"L\\u00e9\\u010dba TDP lampou\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', 15, 0, 'Léčba TDP lampou, akutní bolet zad terapie, akutní bolet zad léčba', 'Lampu TDT v našem fyzioterapeutickém centru Kinisi v Praze 9  využíváme především pro relaxaci svalů před mobilizací při bolestech páteře.', 1, 294, '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', 0, '*', '');
INSERT INTO `bc73q_content` VALUES(225, 892, 'Fyzioterapie pro ženy', 'fyzioterapie-pro-zeny-zakladni-informace', '<div class=\"row bottom-margin-small\">\r\n<div class=\"col-sm-6\">{os-gal-15}</div>\r\n\r\n<div class=\"col-sm-6\">\r\n<p><span class=\"span3 col-6\">V Kinisi se zaměřujeme na ženy toužící otěhotnět, také ženy, které mají v těhotenství potíže s pohybovým aparátem nebo se chtějí udržovat v dobré fyzické kondici. Po porodu je také důležité zabezpečit obnovu funkce svalů pánevního dna, posílit břišní a zádové svaly a obnovit trupovou stabilizaci.</span></p>\r\n\r\n<h6>SOUVISEJÍCÍ</h6>\r\n\r\n<ul class=\"list\">\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=204&amp;catid=99\" title=\" Léčba funkční sterility\">Léčba funkční sterility</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=205&amp;catid=99\" title=\"Fyzioterapie v těhotenství\">Fyzioterapie v těhotenství</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=206&amp;catid=99\" title=\"Fyzioterapie po porodu\">Fyzioterapie po porodu</a></li>\r\n	<li><a href=\"#\">Metoda Ludmily Mojžíšové</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n', '', 1, 83, '2017-10-08 20:11:14', 630, '', '2017-11-26 16:54:48', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"left\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"none\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', 5, 0, '', 'Zaměřujeme se na ženy toužící otěhotnět, ženy, které mají v těhotenství potíže s pohybovým aparátem nebo se chtějí udržovat v dobré fyzické kondici. ', 1, 14, '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', 0, '*', '');
INSERT INTO `bc73q_content` VALUES(226, 893, 'Dětská fyzioterapie', 'detska-fyzioterapie-zakladni-informace', '<div class=\"row bottom-margin-small\">\r\n<div class=\"col-sm-6\">{os-gal-13}</div>\r\n\r\n<div class=\"col-sm-6\">\r\n<p><span class=\"span3 col-6\">Dětská fyzioterapie nemusí být jen řešením již vzniklých chybných pohybových stereotypů, korekce plochonoží či poúrazová péče, ale i jako důležitá prevence před těmito důsledky. Posláním fyzioterapeutů v centru Kinisi je zachytit a ovlivnit odchylky ve vývoji dítěte v co nejmladším věku a nastavit tak jeho další zdravý růst.</span></p>\r\n\r\n<h6>SOUVISEJÍCÍ</h6>\r\n\r\n<ul class=\"list\">\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=195&amp;catid=88\" title=\"Vyšetření\">Vyšetření</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=196&amp;catid=88\" title=\"Indikace\">Indikace</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=197&amp;catid=88\" title=\"Metody a terapeutické přístupy\">Metody a terapeutické přístupy</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n', '', 1, 83, '2017-10-08 20:11:14', 630, '', '2017-11-26 16:38:38', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"left\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"none\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\