INSERT INTO `bc73q_content` VALUES(227, 894, 'Fyzioterapie dospělých', 'fyzioterapie-dospelych-zakladni-info', '<div class=\"row bottom-margin-small\">\r\n<div class=\"col-sm-6\">{os-gal-5}</div>\r\n\r\n<div class=\"col-sm-6\">\r\n<p><span class=\"span3 col-6\">V centru Kinisi vybíráme postup léčby ze široké škály metodik a terapeutických protředků individuálně podle potřeby každého klienta. Našim hlavním cílem je obnovení a normalizace pohybů tak, aby nedocházelo k přetížení a bolestem pohybového systému.</span></p>\r\n\r\n<h6><strong>SOUVISEJÍCÍ</strong></h6>\r\n\r\n<ul class=\"list\">\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=192&amp;catid=84\" title=\"Fyzioterapeutické vyšetření\">Vyšetření</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=193&amp;catid=84\" title=\"Indikace\">Indikace</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=194&amp;catid=84\" title=\"Metody a terapeutické přístupy\">Metody a terapeutické přístupy</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n', '', 1, 83, '2017-10-08 20:11:14', 630, '', '2017-11-26 16:10:31', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"left\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"none\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_da