INSERT INTO `bc73q_content` VALUES(228, 895, 'Péče o sportovce', 'pece-o-sportovce-zakladni-informace', '<div class=\"row bottom-margin-small\">\r\n<div class=\"col-sm-6\">{os-gal-16}</div>\r\n\r\n<div class=\"col-sm-6\">\r\n<p><span class=\"span3 col-6\">Analýza základních pohybů a pohybových vzorů potřebných ke splnění fyzických požadavků daného sportu by měla být nedílnou součástí přípravy sportovců do nadcházející sezóny. Identifikováním všech aspektů svalové dysfunkce v rámci několika pohybových vzorů lze zvolit přesný terapeutický postup pro nejrychlejší bezpečný návrat sportovce do fyzické zátěže.</span></p>\r\n\r\n<h6>SOUVISEJÍCÍ</h6>\r\n\r\n<ul class=\"list\">\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=211&amp;catid=100\" title=\"Komplexní analýza sportovce\">Komplexní analýza sportovce</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=207&amp;catid=100\" title=\"Indikace sportovní fyzioterapie\">Indikace sportovní fyzioterapie</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=208&amp;catid=100\" title=\"Léčba akutních úrazů sportovců\">Léčba akutních úrazů sportovců</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=209&amp;catid=100\" title=\"Preventivní programy sportovců\">Preventivní programy sportovců</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=210&amp;catid=100\" title=\"Metody a terapeutické přístupy u sportovců\">Metody a terapeutické přístupy u sportovců</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n', '', 1, 83, '2017-10-08 20:11:14', 630, '', '2017-11-26 17:10:47', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"left\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"none\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":