INSERT INTO `bc73q_content` VALUES(231, 898, 'Sportovní úrazy a operace', 'sportovni-urazy-a-operace', '<div class=\"row bottom-margin-small\">\r\n<div class=\"col-sm-6\">{os-gal-2}</div>\r\n\r\n<div class=\"col-sm-6\">\r\n<ul class=\"list\">\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=228&amp;catid=83\" title=\"Péče o sportovce (2)\">Základní informace</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=211&amp;catid=100\" title=\"Komplexní analýza sportovce\">Komplexní analýza sportovce</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=208&amp;catid=100\" title=\"Léčba akutních úrazů sportovců\">Léčba akutních úrazů sportovců</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=209&amp;catid=100\" title=\"Preventivní programy sportovců\">Preventivní programy sportovců</a></li>\r\n	<li><a href=\"index.php?option=com_content&amp;view=article&amp;id=210&amp;catid=100\" title=\"Metody a terapeutické přístupy u sportovců\">Metody a terapeutické přístupy u sportovců</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n\r\n<h4>Sportovní úrazy a operace</h4>\r\n\r\n<div class=\"row\">\r\n<div class=\"col-sm-6\">\r\n<h6>Sportovní úrazy</h6>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Svalová kontuze (zhmoždění, narazení) a namožení svalu</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Natažení a natrhnutí svalu</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Distorze kloubů (hlezno, rameno, koleno)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Subluxace a luxace kloubů</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Zlomeniny, únavové zlomeniny</p>\r\n</div>\r\n\r\n<div class=\"col-sm-6\">\r\n<h6>Operace</h6>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Operace páteře (osteosyntéza, mobilní stabilizace páteře, operace meziobratlové ploténky)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Artroskopie kolena (menisků a vazů)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Artroskopie ramena (akromioplastika, subakromiální dekomprese)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Operace hlezenního kloubu (artrodéza, alloplastika hlezna)</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Plastika předního skříženého vazu kolena</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Sutura achillovy šlachy</p>\r\n\r\n<p><i class=\"fa fa-dot-circle-o\"></i>&nbsp; Operace břišní kýly</p>\r\n</div>\r\n</div>\r\n', '', 1, 100, '2017-10-08 20:11:14', 630, '', '2017-11-24 18:44:48', 630, 0, '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', 2, 0, '', 'V fyzioterapeutickém centru v Horních Počernicích v Praze se specializujeme na sportovní fyzioterapii. ', 1, 1, '{\"robots\":\"\",\"author\":\"\",\"rig