INSERT INTO `bc73q_finder_links` VALUES(180, 'index.php?option=com_content&view=article&id=151', 'index.php?option=com_content&view=article&id=151:reference-technology-florbal-mlada-boleslav&catid=66', 'Technology Florbal Mladá Boleslav', ' Technology Florbal Mladá Boleslav Mgr. Martin Vojtovič působí v Technology Florbal Mladá Boleslav od roku 2015 jako hlavní fyzioterapeut mužského A-týmu. Jsme velmi spokojeni s kvalitnou poskytovaných služeb, od probíhající sezóny 2016/2017 plánujeme hlubší rozšíření spolupráce fyzioterapeutických služeb s centrem fyzioterapie Kinisi. ', '2017-11-12 13:08:34', '1ec08e552355d4db0e995387058206f0', 1, 1, 1, '*', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', 0, 0, 4, );
