INSERT INTO `bc73q_finder_links` VALUES(212, 'index.php?option=com_content&view=article&id=166', 'index.php?option=com_content&view=article&id=166:funkcni-fyziotrenink&catid=73', 'Funkční fyziotrénink', ' V Kinisi si v rámci funkčního fyziotréninku klademe za cíl naučit tělo lépe vnímat a provádět jednotlivé pohyby tak, aby docházelo k efektivní účelné svalové aktivaci, zvýšení sportovní výkonnosti, menšímu opotřebení pohybového systému nebo kompenzaci stresové zátěže. Preventivní programy Správné cvičení a posilování Metody a tréninkové prostředky ', '2017-11-14 22:59:46', 'bd7eb90736f69a3e05fb12a4f5c469b9', 1, 1, 1, '*', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', 0, 0, 4, );
INSERT INTO `bc73q_finder_links` VALUES(213, 'index.php?option=com_content&view=article&id=167', 'index.php?option=com_content&view=article&id=167:fyzioterapie-zeny&catid=18', 'Fyzioterapie pro ženy', ' V Kinisi se zaměřujeme na ženy toužící otěhotnět, také ženy, které mají v těhotenství potíže s pohybovým aparátem nebo se chtějí udržovat v dobré fyzické kondici. Po porodu je také důležité zabezpečit obnovu funkce svalů pánevního dna, posílit břišní a zádové svaly a obnovit trupovou stabilizaci. Léčba funkční sterility Fyzioterapie v těhotenství Fyzioterapie po porodu Metoda Ludmily Mojžíšové ', '2017-11-14 23:01:20', '29647c6e18b0bfa9bc9f0989ffdc2d72', 1, 1, 1, '*', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', 0, 0, 4, );
INSERT INTO `bc73q_finder_links` VALUES(214, 'index.php?option=com_content&view=article&id=168', 'index.php?option=com_content&view=article&id=168:pece-o-sportovce&catid=73', 'Péče o sportovce', ' Analýza základních pohybů a pohybových vzorů potřebných ke splnění fyzických požadavků daného sportu by měla být nedílnou součástí přípravy sportovců do nadcházející sezóny. Identifikováním všech aspektů svalové dysfunkce v rámci několika pohybových vzorů lze zvolit přesný terapeutický postup pro nejrychlejší bezpečný návrat sportovce do fyzické zátěže. Komplexní analýza sportovce Indikace sportovní fyzioterapie Léčba akutních úrazů sportovců Preventivní programy sportovců Metody a terapeutické přístupy u sportovců ', '2017-11-14 23:02:52', '20a423ef8eeb2b2deea8b99015a66945', 1, 1, 1, '*', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', 0, 0, 4, );
INSERT INTO `bc73q_finder_links` VALUES(236, 'index.php?option=com_content&view=article&id=18', 'index.php?option=com_content&view=article&id=18:akutni-a-pooperacni-fyzioterapie&catid=73', 'Akutní a pooperační fyzioterapie', ' Akutní bolesti pohybového systému neustále přibývají a narůstá také jejich závažnost. Nejdůležitejším cílem akutní a pooperační fyzioterapie je zvládnutí bolesti, otoku, obnova a zachování plného rozsahu pohybu a péče o jizvu. Akutní fyzioterapie Pooperační fyzioterapie ', '2017-11-21 17:34:53', 'e55ca60b7f8d38284fa2a6ce8fcced7c', 1, 1, 1, '*', '2017-10-08 20:11:14', '0000-00-00 00:00:00', '2017-10-08 20:11:14', '0000-00-00 00:00:00', 0, 0, 4, );
